import React, { PureComponent } from 'react';
import { PieChart, Pie, Sector } from 'recharts';

const tech = [
  { name: 'AAPL', value: 2.08 },
  { name: 'MSFT', value: 1.63 },
  { name: 'AMZN', value: 1.58 },
  { name: 'GOOG', value: 1.2 },
];

const healthCare = [
    { name: 'CVS', value: 257.3 },
    { name: 'UNH', value: 240.3 },
    { name: 'MCK', value: 224.9 },
    { name: 'ABC', value: 182.1 },
  ];

const renderActiveShape = (props) => {
  const RADIAN = Math.PI / 180;
  const {
    cx, cy, midAngle, innerRadius, outerRadius, startAngle, endAngle,
    fill, payload, percent, value,
  } = props;
  const sin = Math.sin(-RADIAN * midAngle);
  const cos = Math.cos(-RADIAN * midAngle);
  const sx = cx + (outerRadius + 10) * cos;
  const sy = cy + (outerRadius + 10) * sin;
  const mx = cx + (outerRadius + 30) * cos;
  const my = cy + (outerRadius + 30) * sin;
  const ex = mx + (cos >= 0 ? 1 : -1) * 22;
  const ey = my;
  const textAnchor = cos >= 0 ? 'start' : 'end';

  return (
    <g>
      <text x={cx} y={cy} dy={8} textAnchor="middle" fill={fill}>{payload.name}</text>
      <Sector
        cx={cx}
        cy={cy}
        innerRadius={innerRadius}
        outerRadius={outerRadius}
        startAngle={startAngle}
        endAngle={endAngle}
        fill={fill}
      />
      <Sector
        cx={cx}
        cy={cy}
        startAngle={startAngle}
        endAngle={endAngle}
        innerRadius={outerRadius + 6}
        outerRadius={outerRadius + 10}
        fill={fill}
      />
      <path d={`M${sx},${sy}L${mx},${my}L${ex},${ey}`} stroke={fill} fill="none" />
      <circle cx={ex} cy={ey} r={2} fill={fill} stroke="none" />
      <text x={ex + (cos >= 0 ? 1 : -1) * 12} y={ey} textAnchor={textAnchor} fill="#333">{`MC ${value}`}</text>
      <text x={ex + (cos >= 0 ? 1 : -1) * 12} y={ey} dy={18} textAnchor={textAnchor} fill="#999">
        {`(${(percent * 100).toFixed(2)}%)`}
      </text>
    </g>
  );
};


export default class Example extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/alidingling/hqnrgxpj/';

  state = {
    activeTech: 0,
    activeHealth: 0
  };

  onTechEnter = (data, index) => {
    this.setState({
      activeTech: index,
    });
  };

  onHealthEnter = (data, index) => {
    this.setState({
      activeHealth: index,
    });
  };

  render() {
    return (
        <div style={{"height":"90vh", "width":"90vw"}}>
            Tech
            <PieChart width={400} height={400}>
                <Pie
                activeIndex={this.state.activeTech}
                activeShape={renderActiveShape}
                data={tech}
                cx={200}
                cy={200}
                innerRadius={60}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
                onMouseEnter={this.onTechEnter}
                />
            </PieChart>
            <PieChart width={400} height={400}>
                <Pie
                activeIndex={this.state.activeHealth}
                activeShape={renderActiveShape}
                data={healthCare}
                cx={200}
                cy={200}
                innerRadius={60}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
                onMouseEnter={this.onHealthEnter}
                />
            </PieChart>
      </div>
    );
  }
}
