import React from "react"
import './Articles.css'

export default class Articles extends React.Component{

    state = {
        news: [],
    }

    componentDidMount() {
        fetch('https://finnhub.io/api/v1/news?category=forex&minId=0&token=bvhs6ef48v6okueau8n0')
        .then(res => res.json())
        .then((data) => {
          this.setState({ news: data })
          this.setState({news: this.state.news.splice(1,10)})
          console.log(this.state.news)
        })
        .catch(console.log);
      }

    render () {
        return(
            <div style={{"display":"flex","overflow-x":"scroll", "align-items":"center", "justifyContent":"center"}}>
                {this.state.news.map(d => (<a class="marquee" key={d.id} href={d.url} target={"_blank"} style={{"width":"20vw"}}>{d.headline}</a>))}
            </div>
        )
    }
}
