import React from "react"

export default class Articles extends React.Component{

    state = {
        news: [],
    }

    componentDidMount() {
        fetch('https://finnhub.io/api/v1/news?category=forex&minId=0&token=bvhs6ef48v6okueau8n0')
        .then(res => res.json())
        .then((data) => {
          this.setState({ news: data })
          this.setState({news: this.state.news.splice(1,10)})
          console.log(this.state.news)
        })
        .catch(console.log);
      }

    render () {
        return(
            <div style={{"display": "block",/* or inline-block */
                "text-overflow": "ellipsis",
                "word-wrap": "break-word",
                "overflow": "hidden",
                "max-height": "3.6em",
                "line-height": "1.8em",
                "lineBreak":"none",
                "gap": "1rem"
                }}>
                {this.state.news.map(d => (<a key={d.id} href={d.url} target={"_blank"}>{d.headline}</a>))}
            </div>
        )
    }
}
